tinymce.PluginManager.add('imageUpload', function (editor, url) {
    function jbBox() {
        editor.windowManager.open({title: 'Upload an image', file: url + '/dialog2.htm', width: 350, height: 135, buttons: [
            {text: 'Upload', classes: 'widget btn primary first abs-layout-item', disabled: true, onclick: 'close'},
            {text: 'Close', onclick: 'close'}
        ]})
    }

    editor.addButton('imageUpload', {tooltip: 'Upload an image', icon: 'image', text: 'Upload', onclick: jbBox});
    editor.addMenuItem('imageUpload', {text: 'Upload image', icon: 'image', context: 'insert', onclick: jbBox})
});